function openChute(dc,eC){
	$.ajax({
	  url: "getAntBot.php", timeout: 3000,
	  success: function(data) {
		if(data=='expired'){
			$("#popKick").show();
			$("#chuts").hide();
			$("#capt").show();
			$("#formCap").find('input[name="captcha"]').focus();
			tmpDc=dc;
