function vai(volta,t,tmp,bD,rQ,aBE,isBE,done){
	dim=Array(5,8,10,15);

	if(done){
		tmpG+=rQ;
		tmpDim=tmpDim+dim[t-1];
	}

	if(volta!='ak'){
