	}
	if(isBE!=1){
		$("#kicks").flash(function(){
				this.SetVariable("/:tmp"+t, tmp);
				this.SetVariable("/:bD"+t, bD);
		});
	}

	if(aBE==1){
		//alert('vai');
		$("#kicksFlash")[0].addBolaExtra();
	}
}


